import React, { Component } from 'react';
import MaterialIcon from 'material-icons-react';

import '../assets/css/components/create-component.css';

export default class CreateComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      street: '',
      streetNumber: '',
      zip: null,
      city: '',
      awesomeText: '',
      file: '',
      imagePreviewUrl: '',
      imageName: '',
      showAlertRequiredFields: false,
    };

    //* Register methods
    this.handleChange = this.handleChange.bind(this);
    this.deleteImage = this.deleteImage.bind(this);
    this.createLocation = this.createLocation.bind(this);
  }

  //* Check inputs for changes
  handleChange(event) {
    //* Find the right state and save content from input into state
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  //*Â Delete image
  deleteImage() {
    this.setState({ imagePreviewUrl: '', imageName: '', file: '' });
  }

  //* Send on Server
  _handleSubmit(event) {
    event.preventDefault();
    console.log('handle submit - ', this.state.file);
  }

  //* Load image
  _handleImageChange(event) {
    event.preventDefault();

    let reader = new FileReader();
    let file = event.target.files[0];

    this.setState({ imageName: file.name });

    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result,
      });
    };

    reader.readAsDataURL(file);
  }

  createLocation(event) {
    //*  Stop button behavior
    event.preventDefault();

    //* Check if all fields are filled
    if (
      this.state.title === '' ||
      this.state.street === '' ||
      this.state.zip === null ||
      this.state.city === ''
    ) {
      //* Not all fields filed show Alert
      this.setState({ showAlertRequiredFields: true });
      window.setTimeout(() => {
        this.setState({ showAlertRequiredFields: false });
      }, 5000);
    }

    //* Redirect to Dashboard
    this.props.history.push('/');
  }

  render() {
    let { imagePreviewUrl } = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = <img src={imagePreviewUrl} alt={this.state.imageName} />;
    } else {
      $imagePreview = <div className="previewText"></div>;
    }

    return (
      <section className="create-component">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4 pt-4">Create New Location</h1>
          {/* START Alert Fields are required */}
          {this.state.showAlertRequiredFields && (
            <div className="alert alert-danger" role="alert">
              Och noo something is wrong with yours required fields.
            </div>
          )}
          {/* END Alert Fields are required */}

          <div className="card">
            <div className="card-body">
              <form>
                {/* START Title */}
                <div className="form-group pt-2">
                  <input
                    type="text"
                    name="title"
                    className="form-control"
                    placeholder="Title"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Title */}

                {/* START Street */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="street"
                    className="form-control"
                    placeholder="Street"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Street */}

                {/* START Street Number */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="streetNumber"
                    className="form-control"
                    placeholder="Street Number"
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Street Number */}

                {/* START Zip */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="number"
                    name="zip"
                    className="form-control"
                    placeholder="Zip"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Zip */}

                {/* START City */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="city"
                    className="form-control"
                    placeholder="City"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END City*/}

                {/* START Textarea */}
                <div className="form-group">
                  <textarea
                    name="awesomeText"
                    className="form-control"
                    placeholder="Write something awesome."
                    rows="5"></textarea>
                </div>
                {/* END Textarea */}

                <div className="image-upload  pt-3 pb-5">
                  <form onSubmit={(e) => this._handleSubmit(e)} className="pr-5">
                    <div className="file btn  btn-outline-primary">
                      Upload
                      <input type="file" name="file" onChange={(e) => this._handleImageChange(e)} />
                    </div>

                    {/* <button
                      className="submitButton"
                      type="submit"
                      onClick={(e) => this._handleSubmit(e)}>
                      Upload Image
                    </button> */}
                  </form>

                  <div className="delete-icon">
                    <MaterialIcon icon="delete" color="#d9534f" onClick={this.deleteImage} />
                    <p className="pl-3">{this.state.imageName}</p>
                  </div>
                  <div className="imgPreview">{$imagePreview}</div>
                </div>

                <button
                  type="submit"
                  className="btn btn-outline-danger"
                  onClick={this.createLocation}>
                  Add
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
