import axios from 'axios';
import { GET_ERRORS } from './types';

export const createPost = (project, history) => async (dispatch) => {
  try {
    const res = await axios.post('http://localhost:8080/api/project', project);
    history.push('/');
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};
