import React, { Component } from 'react';

export default class UsersCard extends Component {

	constructor(props) {
		super(props);
		//* Bully Data fro Test
		this.state = {
			locations: [
				{
					id: 1,
					title: 'Test Title 1',
					street: 'Kapellenstrasse',
					streetNumber: '26',
					city: 'Bern',
					zip: 3011,
					image: 'test1.jpg',
					text: 'douaguiogaug  ouahdo ah d aohdo ahoia h.',
				},
				{
					id: 2,
					title: 'Test Title 2',
					street: 'Eggweg',
					streetNumber: '1',
					city: 'Herisau',
					zip: 9100,
					image: 'test2.jpg',
					text: 'douaguiogaug  ouahdo ah d aohdo ahoia h. hsoudghouqsgdog doiuqg odoqiugd ouq.',
				},
				{
					id: 3,
					title: 'Test Title 3',
					street: 'Poststrasse',
					streetNumber: '10',
					city: 'Herisau',
					zip: 9100,
					image: 'test3.png',
					text: 'douaguiogaug  ouahdo ah d aohdo ahoia h. hsoudghouqsgdog doiuqg odoqiugd ouq.',
				},
			],
		};
	}

  render() {
    return (
      <section className="custom-card">
        <div className="container">
          {this.state.locations.map((item) => (
            <div className="card" key={item.id}>
              <img
                src={require(`../assets/img/${item.image}`)}
                className="card-img-top"
                alt={item.title}
              />
              <div className="card-body">
                <h5 className="card-title">{item.title}</h5>
                <h6>
                  {item.street} {item.streetNumber} {item.zip} {item.city}
                </h6>
                <div className="custom-card__author pb-3">
                  <Link to="/author/{id}">miko866</Link>
                </div>
                <p className="card-text pb-3">{item.text}</p>
                <p className="btn btn-outline-danger text-danger mr-4">View on Map</p>
                <p className="btn btn-outline-primary text-primary mr-5">Edit</p>
                <p className="btn btn-link text-danger">Delete</p>
              </div>
            </div>
          ))}
        </div>
      </section>
    );
  }
}
