import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AuthenticationController from '../controllers/AuthenticationController.js';

import '../assets/css/components/login-component.css';

export default class LoginComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: 'admin@admin.com',
      password: 'admin',
      hasLoginFailed: false,
      showSuccessMessage: false,
    };

    //* Register methods
    this.handleChange = this.handleChange.bind(this);
    this.loginClicked = this.loginClicked.bind(this);
  }

  //* Check inputs for changes
  handleChange(event) {
    //* Find the right state and save content from input into state
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  //* Make login to App
  loginClicked() {
    //* Check the credentials
    if (this.state.email === 'admin@admin.com' && this.state.password === 'admin') {
      //* Call method to save credentials into Sessions
      AuthenticationController.registerSuccessfulLogin(this.state.email, this.state.password);
      //* Redirect to Dashboard
      this.props.history.push('/');
    } else {
      //* If false credentials show Alert message
      this.setState({ hasLoginFailed: true }, () => {
        window.setTimeout(() => {
          this.setState({ hasLoginFailed: false });
        }, 7000);
      });
      this.setState({ showSuccessMessage: false });
    }
  }

  render() {
    return (
      <section className="custom-card-login">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4 pt-4">Login</h1>


          <div className="card">
            <div className="card-body">
              <form>
                {/* START Email */}
                <div className="form-group pt-2">
                  <input
                    type="email"
                    name="email"
                    className="form-control"
                    id="exampleInputEmail1"
                    aria-describedby="emailHelp"
                    placeholder="Email"
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Email */}

                {/* START Password */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="password"
                    name="password"
                    className="form-control"
                    id="exampleInputPassword1"
                    placeholder="Password"
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Password */}
                <button
                  type="submit"
                  className="btn btn-outline-danger"
                  onClick={this.loginClicked}>
                  Login
                </button>

                {/* Forget Password */}
                <Link className="btn btn-link ml-5" to="/resetPassword">
                  <small className="text-primary">Forget Password</small>
                </Link>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
