import axios from 'axios';
import { GET_ERRORS, GET_POSTS } from './types';

//* Create new post in CreateLocation component
export const createPost = (project, history) => async (dispatch) => {
  try {
    // Connection to Server
    const res = await axios.post(process.env.REACT_APP_SERVER, project);
    history.push('/');
  } catch (error) {
    // Save errors into redux
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};

//* Show all posts in LocationCard component
export const getPosts = () => async (dispatch) => {
  // Connection to Server
  const res = await axios.get(process.env.REACT_APP_SERVER + '/allPosts');
  // Save errors into redux
  dispatch({
    type: GET_POSTS,
    payload: res.data,
  });
};
