import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class SinginComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      password1: '',
      password2: '',
      showAlertRequiredFields: false,
      showAlertPassword: false,
    };

    //* Register methods
    this.handleChange = this.handleChange.bind(this);
    this.singinClicked = this.singinClicked.bind(this);
  }

  //* Check inputs for changes
  handleChange(event) {
    //* Find the right state and save content from input into state
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  //* Check inputs for changes
  singinClicked(event) {
    //* Find the right state and save content from input into state
    event.preventDefault();
    if (
      this.state.firstName === '' ||
      this.state.lastName === '' ||
      this.state.email === '' ||
      this.state.password1 === '' ||
      this.state.password2 === ''
    ) {
      this.setState({ showAlertRequiredFields: true });
      window.setTimeout(() => {
        this.setState({ showAlertRequiredFields: false });
      }, 5000);
    }

    if (this.state.password1 !== this.state.password2) {
      this.setState({ showAlertPassword: true });
      window.setTimeout(() => {
        this.setState({ showAlertPassword: false });
      }, 5000);
    }

    sessionStorage.setItem('authenticatedEmail', this.state.email);
    sessionStorage.setItem('authenticatedPassword', this.state.password1);

    this.props.history.push('/');
  }

  render() {
    return (
      <section className="custom-card-singin">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4 pt-4">Sing In</h1>
          {this.state.showAlertRequiredFields && (
            <div className="alert alert-danger" role="alert">
              Och noo something is wrong with yours required fields.
            </div>
          )}
          {this.state.showAlertPassword && (
            <div className="alert alert-danger" role="alert">
              Yours passwords aren't the same.
            </div>
          )}

          <div className="card">
            <div className="card-body">
              <form>
                {/* START First Name */}
                <div className="form-group pt-2">
                  <input
                    type="text"
                    name="firstName"
                    className="form-control"
                    aria-describedby="emailHelp"
                    placeholder="First Name"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END First Name */}

                {/* START Last Name */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="lastName"
                    className="form-control"
                    placeholder="Last Name"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Last Name */}

                {/* START Email */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="email"
                    name="email"
                    className="form-control"
                    placeholder="Email"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Email */}

                {/* START Password */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="password"
                    name="password1"
                    className="form-control"
                    placeholder="Password"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Password */}

                {/* START Password Repeat */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="password"
                    name="password2"
                    className="form-control"
                    placeholder="Repeat Password"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Password repeat*/}

                <button
                  type="submit"
                  className="btn btn-outline-danger"
                  onClick={this.singinClicked}>
                  Sing in
                </button>

                <Link className="btn btn-link ml-5">
                  <small className="text-primary">Login</small>
                </Link>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
