import { GET_POSTS, GET_POST, DELETE_POST } from '../actions/types';

//* Create global state
const initialState = {
  posts: [],
  post: {},
};

//* Save all posts from Server
export default function(state = initialState, action) {
  switch (action.type) {
    case GET_POSTS:
      return {
        ...state,
        posts: action.payload,
      };
    case GET_POST:
      return {
        ...state,
        post: action.payload,
      };
    case DELETE_POST:
      return {
        ...state,
        posts: state.posts.filter((post) => post.id !== action),
      };
    default:
      return state;
  }
}
