class AuthenticationController {
  //* Save data into  SessionStorage
  registerSuccessfulLogin(email, password) {
    sessionStorage.setItem('authenticatedEmail', email);
    sessionStorage.setItem('authenticatedPassword', password);
  }

  //* Remove data from SessionStorage
  logout() {
    window.setTimeout(() => {
      this.setState({ hasLoginFailed: false });
    }, 7000);
    sessionStorage.removeItem('authenticatedEmail');
    sessionStorage.removeItem('authenticatedPassword');
  }

  //* Check if user is logged in
  //* Check SessionStorage and if find data @return true
  isUserLoggedIn() {
    let user = sessionStorage.getItem('authenticatedEmail');

    if (user === null) {
      return false;
    }

    return true;
  }
}

export default new AuthenticationController();
