import React, { Component } from 'react';
import '../assets/css/components/location-card.css';

export default class LocationCard extends Component {

	constructor(props) {
		super(props);
		this.state = {
			locations: [
				{id: 1, title: 'Test Title 1', street: 'Kapellenstrasse', streetNumber: '26', city: 'Bern', zip: 3011, image: 'test1.jpg', text: 'douaguiogaug  ouahdo ah d aohdo ahoia h.' },
				{id: 1, title: 'Test Title 1', street: 'Kapellenstrasse', streetNumber: '26', city: 'Bern', zip: 3011, image: 'test1.jpg', text: 'douaguiogaug  ouahdo ah d aohdo ahoia h.' }
			]
		}
	};

	render() {
		return (
      <section className="custom-card">
        {this.state.locations.map((item) => (
          <div className="card" key={item.id}>
            <img src={require(`../assets/img/${item.image}`)} className="card-img-top" />
            <div className="card-body">
              <h5 className="card-title">{item.title}</h5>
              <h6>
                {item.street} {item.streetNumber} {item.zip} {item.city}
              </h6>
              <div className="custom-card__author">
                <a>miko866</a>
              </div>
              <p className="card-text">{item.text}</p>
              <a className="btn btn-outline-danger text-white mr-5">View on Map</a>
              <a className="btn btn-outline-primary text-white">Edit</a>
            </div>
          </div>
        ))}
      </section>
    );
	}
}
