import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AuthenticationController from '../controllers/AuthenticationController';

import '../assets/css/components/user-card.css';

export default class UsersCard extends Component {
  constructor(props) {
    super(props);
    //* Bully Data for Testing
    this.state = {
      users: [
        {
          id: 1,
          firstName: 'Gandalf',
          lastName: 'White',
          place: '4 Places',
          image: 'gandalf.png',
        },
        {
          id: 2,
          firstName: "It's works",
          lastName: 'Fine',
          place: '2 Places',
          image: 'ItsWork.png',
        },
        {
          id: 3,
          firstName: 'Miko',
          lastName: '866',
          place: '1 Place',
          image: 'miko866.jpg',
        },
      ],
    };
  }

  render() {
    //* Variable with Auth method
    //* Check if user is logged in
    const isUserLoggedIn = AuthenticationController.isUserLoggedIn();

    return (
      <section className="user-card">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4">All Users</h1>
          {/* Render Cards from bully data */}
          {this.state.users.map((item) => (
            <div className="card" key={item.id}>
              <div className="user-card__image">
                <img
                  src={require(`../assets/img/${item.image}`)}
                  alt={item.image}
                  className="custom-image"
                />
              </div>

              <div className="card-body">
                <h4 className="card-title">
                  {item.firstName} {item.lastName}
                </h4>
                <div className="user-card__author pb-3">
                  <p className="custom-color">{item.place}</p>
                </div>
              </div>

              {/* START Button show user locations */}

              <div className="btn btn-primary  user-card__locations">
                <Link to="/user" className="nav-link text-white pr-3">
                  Show
                </Link>
              </div>

              {/* END Button show user locations */}
            </div>
          ))}
        </div>
      </section>
    );
  }
}
