import React, { Component } from 'react';
import MaterialIcon from 'material-icons-react';
import { getPost } from '../actions/projectActions';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import classnames from 'classnames';

class EditLocation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: 0,
      title: '',
      street: '',
      streetNumber: '',
      zip: '',
      text: '',
      image: '',
      imagePreviewUrl: '',
      imageName: '',
      showAlertRequiredFields: false,
      errors: {},
    };
    //* Register methods
    this.handleChange = this.handleChange.bind(this);
    this.deleteImage = this.deleteImage.bind(this);
  }

  componentWillReceiveProps(nextProps) {
    const { id, title, street, streetNumber, zip, text } = nextProps.post;
  }

  componentDidMount() {
    const { id } = this.props.match.params;
    this.props.getPost(id, this.props.history);
  }

  //* Check inputs for changes
  handleChange(event) {
    //* Find the right state and save content from input into state
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  //*Â Delete image
  deleteImage() {
    this.setState({ imagePreviewUrl: '', imageName: '', image: '' });
  }

  //* Send on Server
  _handleSubmit(event) {
    event.preventDefault();
    console.log('handle submit - ', this.state.image);
  }

  //* Load image
  _handleImageChange(event) {
    event.preventDefault();

    //* Read data from HTML element
    let reader = new FileReader();
    //* Find image data into element
    let file = event.target.files[0];

    //* Save image name into state
    this.setState({ imageName: file.name });

    //* After added image find base64 and blob
    reader.onloadend = () => {
      this.setState({
        image: file,
        imagePreviewUrl: reader.result,
      });
    };

    reader.readAsDataURL(file);
  }

  render() {
    //* Set image preview from state
    let { imagePreviewUrl } = this.state;
    let $imagePreview = null;
    //* Create HTML img element with loaded image and show preview if exist
    if (imagePreviewUrl) {
      $imagePreview = <img src={imagePreviewUrl} alt={this.state.imageName} />;
    } else {
      $imagePreview = <div className="previewText"></div>;
    }

    return (
      <section className="create-location">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4 pt-4">Edit Location</h1>

          <div className="card">
            <div className="card-body">
              <form>
                {/* START Title */}
                <div className="form-group pt-2">
                  <input
                    type="text"
                    name="title"
                    className="form-control"
                    placeholder="Title"
                    value={this.state.title}
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Title */}

                {/* START Street */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="street"
                    className="form-control"
                    placeholder="Street"
                    value={this.state.street}
                    onChange={this.handleChange}
                    disabled
                  />
                </div>
                {/* END Street */}

                {/* START Street Number */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="streetNumber"
                    className="form-control"
                    placeholder="Street Number"
                    value={this.state.streetNumber}
                    onChange={this.handleChange}
                    disabled
                  />
                </div>
                {/* END Street Number */}

                {/* START Zip */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="zip"
                    className="form-control"
                    placeholder="Zip"
                    disabled
                    value={this.state.zip}
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Zip */}

                {/* START City */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="city"
                    className="form-control"
                    placeholder="City"
                    disabled
                    value={this.state.city}
                    onChange={this.handleChange}
                  />
                </div>
                {/* END City*/}

                {/* START Textarea */}
                <div className="form-group">
                  <textarea
                    name="text"
                    className="form-control"
                    placeholder="Write something awesome."
                    rows="5"
                    value={this.state.text}
                    onChange={this.handleChange}></textarea>
                </div>
                {/* END Textarea */}

                {/* START Image upload */}
                <div className="image-upload  pt-3 pb-5">
                  <form onSubmit={(e) => this._handleSubmit(e)} className="pr-5">
                    <div className="file btn  btn-outline-primary">
                      Upload
                      <input
                        type="file"
                        name="image"
                        onChange={(e) => this._handleImageChange(e)}
                      />
                    </div>
                  </form>

                  {/* START Delete icon */}
                  <div className="delete-icon">
                    <MaterialIcon icon="delete" color="#d9534f" onClick={this.deleteImage} />
                    <p className="pl-3">{this.state.imageName}</p>
                  </div>
                  {/* END Delete icon */}

                  {/* Show loaded image as preview */}
                  <div className="imgPreview">{$imagePreview}</div>
                </div>
                {/* END Image upload */}

                <button type="submit" className="btn btn-outline-danger">
                  Edit
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

EditLocation.protoTypes = {
  getPost: PropTypes.func.isRequired,
  post: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  post: state.post.post,
});

export default connect(mapStateToProps, { getPost })(EditLocation);
