import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import ReactMapGL, { Marker } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import MaterialIcon from 'material-icons-react';
import '../assets/css/components/map-location.css';
import axios from 'axios';

export default class MapLocation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewport: {
        width: 800,
        height: 500,
        latitude: 0,
        longitude: 0,
        zoom: 16,
      },
      address: '',
      latitude: 0,
      longitude: 0,
      addressNotFound: false,
    };
  }

  componentDidMount() {
    let address = sessionStorage.getItem('address');
    const LIMIT = 2;

    try {
      axios
        .get(
          `https://api3.geo.admin.ch/rest/services/api/SearchServer?searchText=${encodeURI(
            address,
          )}&type=locations&limit=${LIMIT}`,
        )
        .then((response) => {
          // console.log('SWISSTOPO', response.data.results[0].attrs);
          // console.log('STATE', this.state.viewport.latitude);

          if (!response.data.results[0]) {
            return 0;
          }

          this.setState({
            viewport: {
              ...this.state.viewport,
              latitude: response.data.results[0].attrs.lat,
              longitude: response.data.results[0].attrs.lon,
            },
            latitude: response.data.results[0].attrs.lat,
            longitude: response.data.results[0].attrs.lon,
          });
        });
    } catch (error) {
      console.error('Axios error', error);
    }
  }

  render() {
    return (
      <div className=" pt-5">
        <div className="button-position pb-4">
          <div className="btn btn-outline-primary">
            <Link to="/">Back</Link>
          </div>
        </div>

        <div className="map-box">
          <ReactMapGL
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX}
            {...this.state.viewport}
            onViewportChange={(viewport) => this.setState({ viewport })}>
            <Marker
              latitude={this.state.latitude}
              longitude={this.state.longitude}
              offsetLeft={-20}
              offsetTop={-10}>
              <div>
                <MaterialIcon icon="where_to_vote" color="#d9534f" size="large" />
              </div>
            </Marker>
          </ReactMapGL>
        </div>
      </div>
    );
  }
}
