import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class SinginComponent extends Component {
  render() {
    return (
      <section className="custom-card-login">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4 pt-4">Login</h1>
          {/* START Alert */}
          {this.state.hasLoginFailed && (
            <div className="alert alert-warning" role="alert">
              Invalid Credentials
            </div>
          )}
          {this.state.showSuccessMessage && (
            <div className="alert alert-success" role="alert">
              Login successful
            </div>
          )}
          {/* END Alert */}

          <div className="card">
            <div className="card-body">
              <form>
                {/* START Email */}
                <div class="form-group pt-2">
                  <input
                    type="email"
                    name="email"
                    class="form-control"
                    id="exampleInputEmail1"
                    aria-describedby="emailHelp"
                    placeholder="Email"
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Email */}

                {/* START Password */}
                <div class="form-group pt-2 pb-2">
                  <input
                    type="password"
                    name="password"
                    class="form-control"
                    id="exampleInputPassword1"
                    placeholder="Password"
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Password */}
                <button type="submit" class="btn btn-outline-danger" onClick={this.loginClicked}>
                  Login
                </button>
                <Link className="btn btn-link ml-5">
                  <small class="text-primary">Forget Password</small>
                </Link>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
