import React, { Component } from 'react';
import MaterialIcon from 'material-icons-react';

import '../assets/css/components/create-location.css';

export default class UserProfile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      password1: '',
      password2: '',
      file: '',
      imagePreviewUrl: '',
      imageName: '',
      showAlertRequiredFields: false,
    };

    this.handleChange = this.handleChange.bind(this);
    this.deleteImage = this.deleteImage.bind(this);
    this.userProfile = this.userProfile.bind(this);
  }

  //* Check inputs for changes
  handleChange(event) {
    //* Find the right state and save content from input into state
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  deleteImage() {
    this.setState({ imagePreviewUrl: '', imageName: '', file: '' });
  }

  _handleSubmit(event) {
    event.preventDefault();
    console.log('handle submit - ', this.state.file);
  }

  _handleImageChange(event) {
    event.preventDefault();

    let reader = new FileReader();
    let file = event.target.files[0];

    this.setState({ imageName: file.name });

    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result,
      });
    };

    reader.readAsDataURL(file);
  }

  userProfile(event) {
    //*  Stop button behavior
    event.preventDefault();

    //* Check if all fields are filled
    if (
      this.state.firstName === '' ||
      this.state.lastName === '' ||
      this.state.email === '' ||
      this.state.password1 === '' ||
      this.state.password2 === ''
    ) {
      //* Not all fields filed show Alert
      this.setState({ showAlertRequiredFields: true });
      window.setTimeout(() => {
        this.setState({ showAlertRequiredFields: false });
      }, 5000);
    }

    //* Redirect to Dashboard
    this.props.history.push('/');
  }

  render() {
    let { imagePreviewUrl } = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = <img src={imagePreviewUrl} alt={this.state.imageName} />;
    } else {
      $imagePreview = <div className="previewText"></div>;
    }

    return (
      <section className="user-profile">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4 pt-4">Profile</h1>
          {/* START Alert Fields are required */}
          {this.state.showAlertRequiredFields && (
            <div className="alert alert-danger" role="alert">
              Och noo something is wrong with yours required fields.
            </div>
          )}
          {/* END Alert Fields are required */}

          <div className="card">
            <div className="card-body">
              <form>
                {/* START First Name */}
                <div className="form-group pt-2">
                  <input
                    type="text"
                    name="firstName"
                    className="form-control"
                    aria-describedby="emailHelp"
                    placeholder="First Name"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END First Name */}

                {/* START Last Name */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="text"
                    name="lastName"
                    className="form-control"
                    placeholder="Last Name"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Last Name */}

                {/* START Email */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="email"
                    name="email"
                    className="form-control"
                    placeholder="Email"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Email */}

                {/* START Password */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="password"
                    name="password1"
                    className="form-control"
                    placeholder="Password"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Password */}

                {/* START Password Repeat */}
                <div className="form-group pt-2 pb-2">
                  <input
                    type="password"
                    name="password2"
                    className="form-control"
                    placeholder="Repeat Password"
                    required
                    onChange={this.handleChange}
                  />
                </div>
                {/* END Password repeat*/}

                {/* START Image upload */}
                <div className="image-upload  pt-3 pb-5">
                  <form onSubmit={(e) => this._handleSubmit(e)} className="pr-5">
                    <div className="file btn  btn-outline-primary">
                      Upload
                      <input type="file" name="file" onChange={(e) => this._handleImageChange(e)} />
                    </div>
                  </form>

                  {/* START Delete icon */}
                  <div className="delete-icon">
                    <MaterialIcon icon="delete" color="#d9534f" onClick={this.deleteImage} />
                    <p className="pl-3">{this.state.imageName}</p>
                  </div>
                  {/* END Delete icon */}

                  <div className="imgPreview">{$imagePreview}</div>
                </div>
                {/* END Image upload */}

                <button type="submit" className="btn btn-outline-danger" onClick={this.userProfile}>
                  Update
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
