import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AuthenticationController from '../controllers/AuthenticationController';
import { connect } from 'react-redux';
import { getPosts, deletePost } from '../actions/projectActions';
import PropTypes from 'prop-types';
import SweetAlert from 'sweetalert2-react';

import '../assets/css/components/location-card.css';

class LocationCard extends Component {
  constructor(props) {
    super(props);
    //* Bully Data fro Testing
    this.state = {
      showAlert: false,
    };

    //* Register methods
    this.showMap = this.showMap.bind(this);
    this.editLocation = this.editLocation.bind(this);
    this.onDeleteClick = this.onDeleteClick.bind(this);
  }

  onDeleteClick(id) {
    this.props.deletePost(id);
  }

  showMap(street, streetNumber, zip, city) {
    let address = street + ' ' + streetNumber + ' ' + zip + ' ' + city;
    sessionStorage.setItem('address', address);

    //* Redirect to Map with address from card
    this.props.history.push('/map');
  }

  //* Hard reload
  componentDidMount() {
    this.props.getPosts();

    let user = sessionStorage.getItem('authenticatedEmail');
    //* Check if user is logged
    if (user !== '') {
      //* Check localStorage
      if (window.localStorage) {
        //* Exist any item -> firstLoad -> created it
        if (!localStorage.getItem('firstLoad')) {
          localStorage['firstLoad'] = true;
          window.location.reload();
        } else localStorage.removeItem('firstLoad');
      }
    }
  }

  editLocation() {
    this.props.history.push('/editPost/:id');
  }

  render() {
    const { posts } = this.props.post;

    //* Variable with Auth method
    //* Check if user is logged in
    const isUserLoggedIn = AuthenticationController.isUserLoggedIn();

    return (
      <section className="location-card">
        <div className="container">
          {/* Render Cards from bully data */}
          {posts.map((item) => (
            <div className="card" key={item.id}>
              {/* <img
                src={require(`../assets/img/${item.image}`)}
                className="card-img-top"
                alt={item.title}
              /> */}
              <div className="card-body">
                <h2 className="card-title">{item.title}</h2>
                <h5>
                  {item.street} {item.streetNumber} {item.zip} {item.city}
                </h5>
                <div className="location-card__author pb-3">
                  <Link to="/author/{id}">miko866</Link>
                </div>
                <p className="card-text pb-3">{item.text}</p>
                <div
                  to="/map"
                  className="btn btn-outline-danger text-danger mr-4"
                  onClick={() => this.showMap(item.street, item.streetNumber, item.zip, item.city)}>
                  View on Map
                </div>

                {/* Only Logged user can see that */}
                {isUserLoggedIn && (
                  <Link
                    to={`/editPost/${item.id}`}
                    className="btn btn-outline-primary text-primary mr-5">
                    Edit
                  </Link>
                )}
                {isUserLoggedIn && (
                  <div
                    className="btn btn-link text-danger"
                    onClick={() => this.setState({ showAlert: true })}>
                    Delete
                  </div>
                )}
                <SweetAlert
                  show={this.state.showAlert}
                  title="Demo Complex"
                  text="SweetAlert in React"
                  showCancelButton
                  onConfirm={() => {
                    console.log('confirm');
                    this.setState({ showAlert: false });
                  }}
                  onCancel={() => {
                    console.log('cancel');
                    this.setState({ showAlert: false });
                  }}
                  onEscapeKey={() => this.setState({ showAlert: false })}
                  onOutsideClick={() => this.setState({ showAlert: false })}
                />
              </div>
            </div>
          ))}
        </div>
      </section>
    );
  }
}

LocationCard.propsTypes = {
  post: PropTypes.object.isRequired,
  getPosts: PropTypes.func.isRequired,
  deletePost: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  post: state.post,
});

export default connect(mapStateToProps, { getPosts, deletePost })(LocationCard);
