import axios from 'axios';
import { GET_ERRORS, GET_POSTS, GET_POST, DELETE_POST } from './types';

//* Create new post in CreateLocation component
export const createPost = (project, history) => async (dispatch) => {
  try {
    // Connection to Server
    const res = await axios.post('/api/project', project);
    history.push('/');
  } catch (error) {
    // Save errors into redux
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};

//* Show all posts in LocationCard component
export const getPosts = () => async (dispatch) => {
  // Connection to Server
  const res = await axios.get('/api/project/allPosts');
  // Save errors into redux
  dispatch({
    type: GET_POSTS,
    payload: res.data,
  });
};

export const getPost = (id, history) => async (dispatch) => {
  const res = await axios.get(`/api/project/${id}`);
  dispatch({
    type: GET_POST,
    payload: res.data,
  });
};

export const deletePost = (id) => async (dispatch) => {
  if (window.confirm('Are you sure?')) {
    await axios.delete(`/api/project/deletePost/${id}`);
    dispatch({
      type: DELETE_POST,
      payload: id,
    });
  }
};
