import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import '../assets/css/components/location-card.css';

export default class UsersCard extends Component {
  constructor(props) {
    super(props);
    //* Bully Data fro Test
    this.state = {
      users: [
        {
          id: 1,
          firstName: 'Gandalf',
          lastName: 'White',
          place: '4 Places',
          image: 'gandalf.png',
        },
        {
          id: 2,
          firstName: "It's works",
          lastName: 'Fine',
          place: '2 Places',
          image: 'ItsWork.png',
        },
        {
          id: 3,
          firstName: 'Miko',
          lastName: '866',
          place: '1 Place',
          image: 'test3.png',
        },
      ],
    };
  }

  render() {
    return (
      <section className="custom-card">
        <div className="container">
          {this.state.locations.map((item) => (
            <div className="card" key={item.id}>
              <img
                src={require(`../assets/img/${item.image}`)}
                className="card-img-top"
                alt={item.title}
              />
              <div className="card-body">
                <h5 className="card-title">{item.title}</h5>
                <h6>
                  {item.street} {item.streetNumber} {item.zip} {item.city}
                </h6>
                <div className="custom-card__author pb-3">
                  <Link to="/author/{id}">miko866</Link>
                </div>
                <p className="card-text pb-3">{item.text}</p>
                <p className="btn btn-outline-danger text-danger mr-4">View on Map</p>
                <p className="btn btn-outline-primary text-primary mr-5">Edit</p>
                <p className="btn btn-link text-danger">Delete</p>
              </div>
            </div>
          ))}
        </div>
      </section>
    );
  }
}
