import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import '../assets/css/components/user-card.css';

export default class UsersCard extends Component {
  constructor(props) {
    super(props);
    //* Bully Data fro Test
    this.state = {
      users: [
        {
          id: 1,
          firstName: 'Gandalf',
          lastName: 'White',
          place: '4 Places',
          image: 'gandalf.png',
        },
        {
          id: 2,
          firstName: "It's works",
          lastName: 'Fine',
          place: '2 Places',
          image: 'ItsWork.png',
        },
        {
          id: 3,
          firstName: 'Miko',
          lastName: '866',
          place: '1 Place',
          image: 'miko866.jpg',
        },
      ],
    };
  }

  render() {
    return (
      <section className="custom-card">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4">All Users</h1>
          {this.state.users.map((item) => (
            <div className="card" key={item.id}>
              <div className="custom-card__image">
                <img
                  src={require(`../assets/img/${item.image}`)}
                  alt={item.image}
                  className="custom-image"
                />
              </div>

              <div className="card-body">
                <h5 className="card-title">
                  {item.firstName} {item.lastName}
                </h5>
                <div className="custom-card__author pb-3">
                  <p>{item.place}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    );
  }
}
