import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AuthenticationController from '../controllers/AuthenticationController.js';

import '../assets/css/components/login-component.css';

export default class LoginComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: 'admin@admin.com',
      password: 'admin',
      hasLoginFailed: false,
      showSuccessMessage: false,
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    console.log('Handle change: ', e);
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  loginClicked() {
    if (this.state.email === 'admin@admin.com' && this.state.password === 'admin') {
      AuthenticationController.registerSuccessfulLogin(this.state.email, this.state.password);
      this.props.history.push('/dashboard');
    } else {
      this.setState({ hasLoginFailed: true });
      this.setState({ showSuccessMessage: false });
    }
  }

  render() {
    return (
      <section className="custom-card-login">
        <div className="container">
          <h1 className="d-flex justify-content-center pb-4 pt-4">Login</h1>
          {this.state.hasLoginFailed && (
            <div className="alert alert-warning" role="alert">
              Invalid Credentials
            </div>
          )}
          {this.state.showSuccessMessage && (
            <div className="alert alert-success" role="alert">
              Login successful
            </div>
          )}
          <div className="card">
            <div className="card-body">
              <form>
                <div class="form-group pt-2">
                  <input
                    type="email"
                    name="email"
                    class="form-control"
                    id="exampleInputEmail1"
                    aria-describedby="emailHelp"
                    placeholder="Email"
                  />
                </div>
                <div class="form-group pt-2 pb-2">
                  <input
                    type="password"
                    name="password"
                    class="form-control"
                    id="exampleInputPassword1"
                    placeholder="Password"
                  />
                </div>
                <button type="submit" class="btn btn-outline-danger">
                  Login
                </button>
                <Link className="btn btn-link ml-5">
                  <small class="text-primary">Forget Password</small>
                </Link>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
