import React, { Component } from 'react';
import ReactMapGL, { Marker } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import MaterialIcon from 'material-icons-react';

export default class MapLocation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewport: {
        width: 600,
        height: 600,
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 8,
      },
    };
  }

  // componentWillMount() {
  //   this.setState({
  //     height: window.innerHeight + 'px',
  //     width: window.innerWidth + 'px',
  //   });
  // }

  render() {
    return (
      <div className="map-box pt-5">
        <ReactMapGL
          mapboxApiAccessToken={
            'pk.eyJ1IjoibWljaGFsZXZvIiwiYSI6ImNrNDRsaDJ3NzAwMGszbGs5ODhub2hndWQifQ.sT_SZOEkvjFWq8hbRtCbEA'
          }
          {...this.state.viewport}
          onViewportChange={(viewport) => this.setState({ viewport })}>
          <Marker latitude={37.78} longitude={-122.41} offsetLeft={-20} offsetTop={-10}>
            <div>
              <MaterialIcon icon="where_to_vote" color="#d9534f" onClick={this.deleteImage} />
            </div>
          </Marker>
        </ReactMapGL>
      </div>
    );
  }
}
