import { GET_POSTS } from '../actions/types';

const initialState = {
  posts: [],
  post: {},
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_POSTS:
      return {
        ...state,
        posts: action.payload,
      };
    default:
      return state;
  }
}
