import React, { Component } from 'react';
import ReactMapGL, { Marker } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import MaterialIcon from 'material-icons-react';
import '../assets/css/components/map-location.css';

export default class MapLocation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewport: {
        width: 800,
        height: 500,
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 16,
      },
      address: '',
    };
  }

  componentDidMount() {
    let address = sessionStorage.getItem('address');

    this.setState({ address: address });

    console.log('Address', address);
  }

  render() {
    return (
      <div className="map-box pt-5">
        <ReactMapGL
          //TODO ENV Variable !!
          mapboxApiAccessToken={
            'pk.eyJ1IjoibWljaGFsZXZvIiwiYSI6ImNrNDRsaDJ3NzAwMGszbGs5ODhub2hndWQifQ.sT_SZOEkvjFWq8hbRtCbEA'
          }
          {...this.state.viewport}
          onViewportChange={(viewport) => this.setState({ viewport })}>
          <Marker latitude={37.7577} longitude={-122.4376}>
            <div>
              <MaterialIcon icon="where_to_vote" color="#d9534f" size="large" />
            </div>
          </Marker>
        </ReactMapGL>
      </div>
    );
  }
}
